import { useState } from "react";
import Navbar from "../components/navBar";

const FeedbackForm = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here
        setSubmitted(true);
    };

    return (
        <div>
            <div className="h-screen w-screen bg-[#2a2a2a] flex items-center justify-center">
                <div className="bg-white bg-opacity-5 p-8 rounded-lg border border-white border-opacity-10 text-white w-1/4">
                    <h1 className="text-2xl font-bold text-center">Feedback Form</h1>
                    {submitted ? (
                        <p className="text-center mt-4">Thank you for your feedback!</p>
                    ) : (
                        <form onSubmit={handleSubmit} className="mt-4">
                            <div className="mb-4">
                                <label className="block text-white opacity-70 mb-2" htmlFor="name">Name</label>
                                <input 
                                    type="text" 
                                    id="name" 
                                    value={name} 
                                    onChange={(e) => setName(e.target.value)} 
                                    className="w-full p-2 rounded bg-transparent border border-white border-opacity-10 text-white"
                                    required 
                                />
                            </div>
                            <div className="mb-4">
                                <label className="block text-white opacity-70 mb-2" htmlFor="email">Email</label>
                                <input 
                                    type="email" 
                                    id="email" 
                                    value={email} 
                                    onChange={(e) => setEmail(e.target.value)} 
                                    className="w-full p-2 rounded bg-transparent border border-white border-opacity-10 text-white"
                                    required 
                                />
                            </div>
                            <div className="mb-4">
                                <label className="block text-white opacity-70 mb-2" htmlFor="message">Message</label>
                                <textarea 
                                    id="message" 
                                    value={message} 
                                    onChange={(e) => setMessage(e.target.value)} 
                                    className="w-full p-2 rounded bg-transparent border border-white border-opacity-10 text-white"
                                    required 
                                />
                            </div>
                            <button 
                                type="submit" 
                                className="w-full bg-purple-500 hover:bg-purple-600 text-white font-bold py-2 px-4 rounded transition duration-300"
                            >
                                Submit
                            </button>
                        </form>
                    )}
                </div>
            </div>
        </div>
    );
};

export default FeedbackForm;
