import { useContext, useState } from "react";
import { AuthContext } from "../config/AuthProvider";
import { useNavigate } from "react-router-dom";

import Navbar from "../components/navBar";
import Loading from "../components/loadingScreen";

import MailImage from "../imgs/mailImage.svg";


const VerifyEmail = () => {
    const { loading, user } = useContext(AuthContext);
    const navigate = useNavigate();

    if (loading) {
        return (
            <Loading />
        );
    }


    return (
        <div>
            <Navbar />
            <div className="h-screen w-screen bg-[#2a2a2a] flex items-center justify-center">
                <div className="bg-white bg-opacity-5 p-8 rounded-lg border border-white border-opacity-10 text-white w-1/3">
                <div className = "flex flex-col">
                    <div className = "w-32 border-white border-opacity-10 rounded-full mx-auto bg-white bg-opacity-10 p-10">
                        <img src={MailImage} alt="Mail Image" className="mx-auto" />
                    </div>
                </div>
                    <h1 className="text-2xl font-bold text-center">Verify Email</h1>
                    <p className="text-center text-white opacity-70">A verification email has been sent to your email address. Please verify your email address to continue.</p>
                </div>
            </div>
        </div>
    );
};

export default VerifyEmail;
