import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

const ProtectedRoute = ({ element: Component, ...rest }) => {
    const user = useSelector((state) => state.auth.value);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const timer = setTimeout(() => {
            setLoading(false);
        }, 3000); // 3 seconds timeout

        if (user !== undefined) {
            setLoading(false);
        }

        return () => clearTimeout(timer);
    }, [user]);

    if (loading) {
        return (
            <div className="w-screen h-screen bg-[#2a2a2a] flex items-center justify-center">
<div class="animate-spin inline-block size-6 border-[3px] border-current border-t-transparent text-green-600 rounded-full" role="status" aria-label="loading">
  <span class="sr-only">Loading...</span>
</div>
            </div>
        )
    }

    return user ? Component : <Navigate to="/login" />;
};

export default ProtectedRoute;
