import PropTypes from "prop-types";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

import Loading from "../components/loadingScreen";
import { getUserByApiKey } from "./databaseRoutes";

const ApiPrivateRoute = ({ children }) => {
    const { apiKey } = useParams();
    const [loading, setLoading] = useState(true);
    const [userExists, setUserExists] = useState(false);

    useEffect(() => {
        const checkUser = async () => {
            try {
                const user = await getUserByApiKey(apiKey);
                if (user) {
                    setUserExists(true);
                }
            } catch (error) {
                console.error("Error fetching user by API key:", error);
            } finally {
                setLoading(false);
            }
        };

        checkUser();
    }, [apiKey]);

    if (loading) {
        return <Loading />;
    }

    return userExists ? children : <h1>Cannot find user</h1>;
};

ApiPrivateRoute.propTypes = {
    children: PropTypes.node,
};

export default ApiPrivateRoute;
